#site-title a {
    opacity: 0.75;
}
#cust-search-wrapper{
  overflow:hidden;
  width:100%;
}
#cust-search-text{
  float:left;
  width:70%;
  padding:5px;
}
#cust-search-form{
  float:right;
  width:30%;
  padding:5px;
}
#cust-search-form-field{
  width:100%;
}
#cust-search-result{
  float:left;
  width:70%;
  padding:5px;
  padding-top:20px;
  min-height:100px;
}
#cust-search-map{
  float:left;
  width:70%;
  padding:5px;
  max-height:400px;
}
#cust-search-map-result {
  width:100%;
  height:350px;
  border-radius: 6px;
  -webkit-border-radius: 6px;
  -moz-border-radius: 6px;
}
#cust-search-map-result img {
  max-width: none;
}
.cust-search-form-panel{
  width:100%;
  background-color:#FFFFFF;
  border-radius: 3px 3px 3px 3px;
  padding:10px;
}

.cust-search-form-panel label{
  color:#FFFFFF;
}

.cust-search-form-panel input, select{
  border:none;
  background-color: #EFEFEF;
  border-radius: 3px 3px 3px 3px;
  width:100% !important;
  margin-left: none !important;
  margin-right: none !important;
}
.awpqsf_class select, input {
    margin-left: 0% !important;
    margin-right: 0% !important;
}

.awpqsf_submit {
    padding-left: 50px;
    padding-right: 50px;
}
.awpqsf_submit input {
  color: #FFFFFF;
  background-color: #3A87AD;
  border-color: #FFFFFF;
  margin-top: 15px;
  float: none;
}
.mloading {
  background-color:inherit !important;
}

#awpqsf_id {
    border: none;
}

#awpqsf_id .form_title{
    display: none;
}

input#awpqsf_id_key {
    margin-left: none;
    margin-right: none;
}

.awpqsf_class > label[class*="tax-label-"], .awpqsf_class > label[class*="tcmf-label-"],.awpqsf_class > label[class*="awpqsf_class-keyword"] {
    display:none;
}

.awpqsf_class select {
    margin-left: none;
    margin-right: none;
    width: 100%%;
}


.max-lines {
    overflow: hidden;
  text-overflow: ellipsis;
  word-wrap: break-word;
  overflow: hidden;
  max-height: 2.8em;
  line-height: 1.4em;
}

@media (max-width: 768px) {
#cust-search-text{
  float:left;
  width:100%;
  padding:5px;
}
#cust-search-form{
  float:left;
  width:100%;
  padding:5px;
}
#cust-search-result{
  float:left;
  width:100%;
  padding:5px;
  min-height:100px;
}
}

.awpqsf_class:not(.awpqsf_submit) {
  float:left;
  width:100%;
  padding:5px;
}

.post-category{
    display:none;
}

.left-menu-open {
    left: 133px;
    position: relative;
}
.left-sidebar #secondary, #left-nav {
    left: -133px;
    width: 133px;
}

.offcanvas #site-navigation li a {
    background: none repeat scroll 0 0 #EFEFEF;
    border-bottom: 1px solid #E1E1E1;
    border-top: 1px solid #E1E1E1;
    color: #333333;
    padding: 12.5px;
}

@media (max-width: 768px) {
  .home-widget img {
    max-width: 40%;
  }
}

.home #header-wrap {
  height: 300px;
}
@media (max-width: 768px) {
  .home #header-wrap {
    height: 200px;
  }
}
#site-title a {
  font-size: 78px;
}
.menu-item-search {
        color: red;
}
@media (max-width: 768px) {
  #site-title a {
    font-size: 45px;
  }
}
#site-description {
  line-height: 1;
}
.navbar-inverse .navbar-inner {
    opacity: .60; /* Standard: FF gt 1.5, Opera, Safari, CSS3 */
    filter: alpha(opacity=60); /* IE lt 8 */
    -ms-filter: "alpha(opacity=60)"; /* IE 8 */
    -khtml-opacity: .60; /* Safari 1.x */
    -moz-opacity: .60; /* FF lt 1.5, Netscape */
    background-image: linear-gradient(to bottom, #E1E1E1, #FEFEFE);
    border-color: #FEFEFE;
}

.navbar-inverse .brand, .navbar-inverse .nav > li > a {
    color: #333333;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
}
.navbar-inverse .brand, .navbar-inverse .nav > li > a:hover {
    color: #111111;
    background-color: #FEFEFE;
    opacity: .60; /* Standard: FF gt 1.5, Opera, Safari, CSS3 */
    filter: alpha(opacity=60); /* IE lt 8 */
    -ms-filter: "alpha(opacity=60)"; /* IE 8 */
    -khtml-opacity: .60; /* Safari 1.x */
    -moz-opacity: .60; /* FF lt 1.5, Netscape */
}

#menu-item-search-cust #searchsubmit {
  background: none;
  border: none;
  color: #999999;
  -webkit-border-radius: 4px;
  -moz-border-radius: 4px;
  -ms-border-radius: 4px;
  -o-border-radius: 4px;
  border-radius: 4px;
  text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
  float: none;
  padding-bottom: 10px;
  padding-left: 15px;
  padding-right: 15px;
  padding-top: 10px;
  vertical-align: baseline;
}
#menu-item-search-cust input[type="text"] {
  color: #999999;
  background-color: #404040;
  border-color: #252525;
  -webkit-border-radius: 4px;
  -moz-border-radius: 4px;
  -ms-border-radius: 4px;
  -o-border-radius: 4px;
  border-radius: 4px;
  width:120px;
  float: none;
  padding-bottom: 10px;
  padding-left: 15px;
  padding-right: 15px;
  padding-top: 10px;
  vertical-align: baseline;
}
@media (max-width: 768px) {
  #menu-item-search-cust {
    display: none;
  }
}

.cust_wrapper { 
  overflow:hidden;
  width:100%;
}
#cust-img  {
  float:right;
  width:30%;
  padding:5px;
}
#cust-img img {
  width:100%;
}
#cust-content {
   float:left;
   width:70%;
   padding:5px;
}

#cust-contact  {
  float:right;
  width:30%;
  padding:5px;
}

#cust-map {
  float:left;
  width:70%;
  padding:5px;
  height:350px;
  border-radius: 6px;
  -webkit-border-radius: 6px;
  -moz-border-radius: 6px;
}
#cust-map img {
  max-width: none;
}
@media (max-width: 768px) {
  #cust-img  {
    float:center;
    width:100%;
    padding:2px;
  }
  #cust-content {
    float:left;
    width:100%;
    padding:2px;
  }
  #cust-contact  {
    float:left;
    width:100%;
    padding:2px;
    padding-top:0px;
  }
  #cust-map {
    float:left;
    width:100%;
    padding:2px;
    height:250px;
    border-radius: 6px;
    -webkit-border-radius: 6px;
    -moz-border-radius: 6px;
  }
  
}
